import React from "react"
import styles from "./styles.module.scss";
import Image from "../../atoms/Image";

const Tecnology = ({ image, title, text }) => {

    window.addEventListener("scroll", () => {
        console.log(document.getElementsByClassName(styles.tecnology)[0].getBoundingClientRect().top)
        console.log(window.pageYOffset)

        if (window.pageYOffset >= document.getElementsByClassName(styles.tecnology)[0]/*.getBoundingClientRect()*/ && !document.getElementsByClassName(styles.tecnology)[0].classList.contains(styles.animationStart)) {
            console.log(document.getElementsByClassName(styles.tecnology)[0].getBoundingClientRect())
            document.getElementsByClassName(styles.tecnology)[0].classList.toggle(styles.animationStart)
        }
    })

    //window.requestAnimationFrame(insertAnimationClass)


    return (
        <div className={styles.tecnology}>
            <div className={`${styles.tecnologyImage} ${image === "react.svg" ? styles.animateRotation : ""}`}>
                <Image className={image === "react.svg" ? styles.animateRotation : ""} alt={image} width={"150px"} src={image} />
            </div>
                <div className={styles.tecnologyContent}>
                <h2>{title}</h2>
                <p>{text}</p>
            </div>
        </div>
    )
}

export default Tecnology
