import React from "react"

const Button = ({ text, color, onClick, border = "0px" }) => {
    const getColor = () => {
        switch (color){
            case "primary":
                return "#ffffff"
            case "secondary":
                return "#282c34"
            default:
                return "#342A21"
        }
    }

    const getBackgroundColor = () => {
        switch (color) {
            case "primary":
                return "#282c34"
            case "secondary":
                return "#ffffff"
            default:
                return "#342A21"
        }
    }

    return (
        <button style={{ border: `${border} solid ${getColor()}`, backgroundColor: `${getBackgroundColor()}`, color: `${getColor()}` }}
                onClick={() => onClick()} >
            {text}
        </button>
    )
}

export default Button
