import React from "react"

const Text = ({ title = null, bodyText, color = "primary" }) => {

    const getColor = () => {
        if (color === "primary") {
            return "#342A21"
        }

        return "#FFFFFF"
    }

    return (
        <div style={{ color: `${getColor()}` }}>
            <h1>{{title}}</h1>
            <p>{{bodyText}}</p>
        </div>
    )
}

export default Text
