import React from "react"
import styles from "./styles.module.scss"
import Tecnology from "../../molecules/Tecnology";
import {isElement} from "react-dom/test-utils";

const Tecnologies = () => {

    window.addEventListener("scroll", () => {
        if (window.pageYOffset >= 690 && !document.getElementsByClassName(styles.tecnologiesContainer).classList.contains(styles.animationStart)) {
            console.log()
            document.getElementsByClassName(styles.tecnologiesContainer)[0].classList.toggle(styles.tecnologiesContainer, styles.tecnologiesContainer)
        }
    })

    //window.requestAnimationFrame(insertAnimationClass)


    return (
        <div id={"tecnologies"} className={styles.tecnologiesContainer}>
            <h1>Tecnologias utilizadas:</h1>
            <Tecnology image={"react.svg"} title={"React.js"} text={"Utilizei esse framework pois facilita a componentização e consequentemente facilita uma desejada ampliação de conteúdo nesta SPA"} />
        </div>
    )
}

export default Tecnologies
