import React from "react"
import "./styles.module.scss"
import LinkNavigation from "../../atoms/LinkNavigation"

const NavBar = () => {

    let prevScrollpos = window.pageYOffset

    window.onscroll = function() {
        let currentScrollPos = window.pageYOffset

        if (prevScrollpos > currentScrollPos) {
            document.getElementsByTagName("nav")[0].style.top = "0"
        } else {
            document.getElementsByTagName("nav")[0].style.top = "-60px"
        }

        prevScrollpos = currentScrollPos
    }

    const scrollToDiv = () => {
        console.log(document.getElementById("tecnologiesId"))
        window.screenTop(document.getElementById("tecnologiesId").offsetTop)
    }

    return (
        <nav>
            <div className={`nav-left`} >
            </div>
            <div className={`nav-right`} >
                <LinkNavigation onClick={scrollToDiv()} text="Meu Pessoal" />
                <LinkNavigation onClick={scrollToDiv()} text="Profissional" />
                <LinkNavigation onClick={scrollToDiv()} text="Este Site" />
                <LinkNavigation onClick={scrollToDiv()} text={"Tecnologias"} />
                <LinkNavigation onClick={scrollToDiv()} text={"Contato"} />
            </div>
        </nav>
    )
}

export default NavBar
