import React from "react"
import styles from "./styles.module.scss"
import classNames from "classnames"

const Button = ({ text, color, onClick, border = "0px", width, height, fontSize }) => {
    const getClass = async => {
        const cssClass = import("./styles.module.scss").then(imported => {
            console.log("Inside promise")
            console.log(imported.default[`${color}`])
            return imported.default[`${color}`]
        })

        console.log(cssClass)
        return cssClass
    }

    return (
        <button style={{ border: `${border}`,
            width: `${width}`,
            height: `${height}`,
            fontSize: `${fontSize}`
        }}
                onClick={() => onClick()}
                className={getClass()}>
            {text}
        </button>
    )
}

export default Button
